import React from 'react';
import { Link } from 'react-router-dom';
import AdBanner from './AdBanner';
import heroImage from '../Assets/YourImage.png'; // Match exact case

const Hero = () => {
  return (
    <>
      {/* Dynamic Ad Banner */}
      <AdBanner />

      <section className="relative h-[80vh] flex flex-col md:flex-row items-center justify-between px-4 sm:px-10 py-10 bg-white">
        {/* Left Text Section */}
        <div className="flex-1 text-center md:text-left">
          <h1 className="text-4xl font-bold mb-4 text-gray-800">
            Empower Your Learning Journey
          </h1>
          <p className="text-lg text-gray-600 mb-6">
            Join Edzest and take your skills to the next level with expert-curated content and mock tests.
          </p>
          <Link to="/explore">
            <button className="bg-blue-600 text-white px-6 py-3 rounded-full hover:bg-blue-700 transition">
              Explore Now
            </button>
          </Link>
        </div>

        {/* Right Image Section */}
        <div className="flex-1 flex justify-end mt-8 md:mt-0">
          <img
            src={heroImage}
            alt="Hero visual"
            className="w-full max-w-md rounded-xl shadow-xl object-cover"
          />
        </div>
      </section>
    </>
  );
};

export default Hero;
